CREATE DATABASE IF NOT EXISTS COUCHOUD_GRODECOEUR_CC;
USE COUCHOUD_GRODECOEUR_CC;
CREATE TABLE IF NOT EXISTS User(ID INT AUTO_INCREMENT, Username VARCHAR(50) NOT NULL, Password VARCHAR(50) NOT NULL, PRIMARY KEY(ID)) ENGINE=INNODB;
CREATE TABLE IF NOT EXISTS File(ID INT AUTO_INCREMENT, Type ENUM('F','D'), Parent INT, Name VARCHAR(255) NOT NULL, Size BIGINT, Owner INT NOT NULL, PRIMARY KEY(ID), FOREIGN KEY (Parent) REFERENCES File(ID) ON DELETE CASCADE, FOREIGN KEY (Owner) REFERENCES User(ID) ON DELETE RESTRICT) ENGINE=INNODB;